INSERT INTO `gen_template` VALUES (179, 28, 'vue/api.ts.ftl', '${frontendPath}/src/api/${functionName}.ts', '后端接口api', 'import service from \'@/utils/request\'\n\n//提交表单\nexport const ${functionName}SubmitApi = (dataForm: any) => {\n    if (dataForm.id) {\n        //修改接口\n        return service.put(\'/${functionName}/update\', dataForm)\n    } else {\n        //新增接口\n        return service.post(\'/${functionName}/add\', dataForm)\n    }\n}\n\n//删除接口\nexport const ${functionName}DeleteApi = (id: number) => {\n    return service.delete(\'/${functionName}/delete?id=\' + id)\n}\n\n//批量删除\nexport const ${functionName}DeleteListApi = (idList: Array<number>) => {\n    return service.delete(\'/${functionName}/deleteList\', { data: idList })\n}\n\n//查询详情\nexport const ${functionName}DetailApi = (id: number) => {\n    return service.get(\'/${functionName}/detail?id=\' + id)\n}\n\n//批量查询\nexport const ${functionName}DetailListApi = (idList: Array<number>) => {\n    return service.post(\'/${functionName}/detailList\', idList)\n}\n\n//简单分页\nexport const ${functionName}EntityPageApi = (queryForm: any) => {\n    return service.post(\'/${functionName}/entityPage\', queryForm)\n}\n\n//简单列表\nexport const ${functionName}EntityListApi = (queryForm: any) => {\n    return service.post(\'/${functionName}/entityList\', queryForm)\n}\n\n//复杂分页\nexport const ${functionName}VOPageApi = (queryForm: any) => {\n    return service.post(\'/${functionName}/voPage\', queryForm)\n}\n\n//复杂列表\nexport const ${functionName}VOListApi = (queryForm: any) => {\n    return service.post(\'/${functionName}/voList\', queryForm)\n}', 0, '2025-05-14 17:55:56', '2025-05-20 11:11:47', 0);
INSERT INTO `gen_template` VALUES (180, 28, 'vue/add-or-update.vue.ftl', '${frontendPath}/src/views/${functionNameKebabCase}/add-or-update.vue', '新增或者修改页面', '<template>\n	<el-dialog v-model=\"visible\" :title=\"!dataForm.id ? \'新增\' : \'修改\'\" :close-on-click-modal=\"false\">\n		<el-form ref=\"dataFormRef\" :model=\"dataForm\" :rules=\"dataRules\" label-width=\"100px\" @keyup.enter=\"submitHandle()\">\n	    <#list formList as field>\n            <el-form-item label=\"${field.fieldComment!}\" prop=\"${field.attrName}\">\n			<#if field.formType == \'text\'>\n                <el-input v-model=\"dataForm.${field.attrName}\" placeholder=\"请输入${field.fieldComment!}\"></el-input>\n			<#elseif field.formType == \'textarea\'>\n                <el-input type=\"textarea\" v-model=\"dataForm.${field.attrName}\"></el-input>\n			<#elseif field.formType == \'editor\'>\n                <el-input type=\"textarea\" v-model=\"dataForm.${field.attrName}\"></el-input>\n			<#elseif field.formType == \'select\'>\n                <el-select v-model=\"dataForm.${field.attrName}\" clearable placeholder=\"请选择${field.fieldComment!}\">\n                    <el-option v-for=\"item in ${field.enumNameAllUpper}_ENUM\" :key=\"item.value\" :label=\"item.label\" :value=\"item.value\"></el-option>\n                </el-select>\n			<#elseif field.formType == \'radio\'>\n                <el-radio-group v-model=\"dataForm.${field.attrName}\">\n                    <el-radio v-for=\"item in ${field.enumNameAllUpper}_ENUM\" :key=\"item.value\" :label=\"item.value\">{{ item.label }}</el-radio>\n                </el-radio-group>\n			<#elseif field.formType == \'checkbox\'>\n                <el-checkbox-group v-model=\"dataForm.${field.attrName}\">\n                    <el-checkbox v-for=\"item in ${field.enumNameAllUpper}_ENUM\" :key=\"item.value\" :label=\"item.label\" :value=\"item.value\">{{ item.label }}</el-checkbox>\n                </el-checkbox-group>\n			<#elseif field.formType == \'date\'>\n                <el-date-picker type=\"date\" placeholder=\"请选择${field.fieldComment!}\" v-model=\"dataForm.${field.attrName}\"></el-date-picker>\n			<#elseif field.formType == \'datetime\'>\n                <el-date-picker type=\"datetime\" placeholder=\"请选择${field.fieldComment!}\" v-model=\"dataForm.${field.attrName}\"></el-date-picker>\n			<#else>\n                <el-input v-model=\"dataForm.${field.attrName}\" placeholder=\"请输入${field.fieldComment!}\"></el-input>\n			</#if>\n            </el-form-item>\n	    </#list>\n		</el-form>\n		<template #footer>\n			<el-button @click=\"visible = false\">取消</el-button>\n			<el-button type=\"primary\" @click=\"submitHandle()\">确定</el-button>\n		</template>\n	</el-dialog>\n</template>\n\n<script setup lang=\"ts\">\nimport { reactive, ref } from \'vue\'\nimport { ElMessage } from \'element-plus/es\'\nimport { ${functionName}DetailApi, ${functionName}SubmitApi } from \'@/api/${functionName}\'\n<#list formList as field>\n    <#if field.formType == \'select\' || field.formType == \'checkbox\' || field.formType == \'radio\'>\nimport { ${field.enumNameAllUpper}_ENUM } from \'@/enums/${field.enumName}-enum\'\n    </#if>\n</#list>\n\nconst emit = defineEmits([\'refreshDataList\'])\n\nconst visible = ref(false)\nconst dataFormRef = ref()\n\nconst dataForm = reactive({\n	<#list fieldList as field>\n	    <#if field.baseField == 0>\n	${field.attrName}: \'\'<#sep>,\n	    </#if>\n	</#list>\n})\n\nconst init = (id?: number) => {\n	visible.value = true\n	dataForm.id = null\n\n	// 重置表单数据\n	if (dataFormRef.value) {\n		dataFormRef.value.resetFields()\n	}\n\n	if (id) {\n		get${functionNamePascal}(id)\n	}\n}\n\nconst get${functionNamePascal} = (id: number) => {\n	${functionName}DetailApi(id).then(res => {\n		Object.assign(dataForm, res.data)\n	})\n}\n\nconst dataRules = ref({\n	<#list formList as field>\n	<#if field.formRequired == 1>\n	${field.attrName}: [{ required: true, message: \'必填项不能为空\', trigger: \'blur\' }]<#sep>,\n	</#if>\n	</#list>\n})\n\n// 表单提交\nconst submitHandle = () => {\n	dataFormRef.value.validate((valid: boolean) => {\n		if (!valid) {\n			return false\n		}\n\n		${functionName}SubmitApi(dataForm).then(() => {\n			ElMessage.success({\n				message: \'操作成功\',\n				duration: 500,\n				onClose: () => {\n					visible.value = false\n					emit(\'refreshDataList\')\n				}\n			})\n		})\n	})\n}\n\ndefineExpose({\n	init\n})\n</script>\n', 0, '2025-05-14 17:55:56', '2025-05-21 17:37:42', 0);
INSERT INTO `gen_template` VALUES (181, 28, 'vue/index.vue.ftl', '${frontendPath}/src/views/${functionNameKebabCase}/index.vue', '列表页面', '<template>\n	<el-card class=\"layout-query\">\n		<el-form ref=\"queryRef\" :inline=\"true\" :model=\"state.queryForm\" @keyup.enter=\"getDataList()\">\n		<#list queryList as field>\n			<el-form-item prop=\"${field.attrName}\">\n			<#if field.queryFormType == \'text\' || field.queryFormType == \'textarea\' || field.queryFormType == \'editor\'>\n                <el-input v-model=\"state.queryForm.${field.attrName}\" clearable placeholder=\"请输入${field.fieldComment}\"></el-input>\n			<#elseif field.formType == \'select\'>\n                <el-select v-model=\"state.queryForm.${field.attrName}\" clearable placeholder=\"请选择${field.fieldComment}\">\n                    <el-option v-for=\"item in ${enumNameAllUpper}_ENUM\" :key=\"item.value\" :label=\"item.label\" :value=\"item.value\"></el-option>\n                </el-select>\n			<#elseif field.formType == \'radio\'>\n                <el-radio-group v-model=\"state.queryForm.${field.attrName}\">\n                    <el-radio v-for=\"item in ${enumNameAllUpper}_ENUM\" :key=\"item.value\" :label=\"item.value\">{{ item.label }}</el-radio>\n                </el-radio-group>\n			<#elseif field.formType == \'checkbox\'>\n                <el-checkbox-group v-model=\"state.queryForm.${field.attrName}\">\n                    <el-checkbox v-for=\"item in ${enumNameAllUpper}_ENUM\" :key=\"item.value\" :label=\"item.label\" :value=\"item.value\">{{ item.label }}</el-checkbox>\n                </el-checkbox-group>\n			<#elseif field.queryFormType == \'date\'>\n                <el-date-picker\n                        v-model=\"state.queryForm.${field.attrName}\"\n                        type=\"daterange\"\n                        format=\"YYYY-MM-DD\"\n                        value-format=\"YYYY-MM-DD\"\n                        clearable\n                >\n                </el-date-picker>\n            <#elseif field.queryFormType == \'datetime\'>\n                <el-date-picker\n                        v-model=\"state.queryForm.${field.attrName}\"\n                        type=\"datetimerange\"\n                        format=\"YYYY-MM-DD HH:mm:ss\"\n                        value-format=\"YYYY-MM-DD HH:mm:ss\"\n                        clearable\n                >\n                </el-date-picker>\n			<#else>\n                <el-input v-model=\"state.queryForm.${field.attrName}\" placeholder=\"请输入${field.fieldComment!}\"></el-input>\n			</#if>\n			</el-form-item>\n        </#list>\n			<el-form-item>\n				<el-button type=\"primary\" @click=\"getDataList()\">查询</el-button>\n			</el-form-item>\n			<el-form-item>\n				<el-button @click=\"resetQueryRef()\">重置</el-button>\n			</el-form-item>\n            <el-form-item>\n                <el-button type=\"primary\" @click=\"addOrUpdateHandle()\">新增</el-button>\n            </el-form-item>\n            <el-form-item>\n                <el-button type=\"danger\" @click=\"deleteBatchHandle()\">删除</el-button>\n            </el-form-item>\n		</el-form>\n	</el-card>\n\n	<el-card>\n		<el-table v-loading=\"state.dataListLoading\" :data=\"state.dataList\" border class=\"layout-table\" @selection-change=\"selectionChangeHandle\">\n			<el-table-column type=\"selection\" header-align=\"center\" align=\"center\" width=\"50\"></el-table-column>\n			<el-table-column type=\"index\" label=\"序号\" header-align=\"center\" align=\"center\" width=\"60\"></el-table-column>\n	    <#list gridList as field>\n		  <#if field.formType == \'select\' || field.formType == \'radio\' || field.formType == \'checkbox\'>\n        	<el-table-column prop=\"${field.attrName}\" label=\"${field.fieldComment!}\" show-overflow-tooltip header-align=\"center\" align=\"center\" :formatter=\"(_: any, __: any, value: any) => getLabel(value, ${field.enumNameAllUpper}_ENUM)\"></el-table-column>\n		  <#else>\n			<el-table-column prop=\"${field.attrName}\" label=\"${field.fieldComment!}\" show-overflow-tooltip header-align=\"center\" align=\"center\"></el-table-column>\n		  </#if>\n        </#list>\n			<el-table-column label=\"操作\" fixed=\"right\" header-align=\"center\" align=\"center\" width=\"150\">\n				<template #default=\"scope\">\n					<el-button type=\"primary\" link @click=\"addOrUpdateHandle(scope.row.id)\">修改</el-button>\n					<el-button type=\"primary\" link @click=\"deleteBatchHandle(scope.row.id)\">删除</el-button>\n				</template>\n			</el-table-column>\n		</el-table>\n		<el-pagination\n			:current-page=\"state.pageNum\"\n			:page-sizes=\"state.pageSizes\"\n			:page-size=\"state.pageSize\"\n			:total=\"state.total\"\n			layout=\"total, sizes, prev, pager, next, jumper\"\n			@size-change=\"sizeChangeHandle\"\n			@current-change=\"currentChangeHandle\"\n		>\n		</el-pagination>\n\n		<!-- 弹窗, 新增 / 修改 -->\n		<add-or-update ref=\"addOrUpdateRef\" @refresh-data-list=\"getDataList\"></add-or-update>\n	</el-card>\n</template>\n\n<script setup lang=\"ts\">\nimport { useCrud } from \'@/hooks\'\nimport { reactive, ref } from \'vue\'\nimport { IHooksOptions } from \'@/hooks/interface\'\nimport AddOrUpdate from \'./add-or-update.vue\'\n<#list gridList as field>\n	<#if field.formType == \'select\' || field.formType == \'radio\' || field.formType == \'checkbox\'>\nimport { getLabel } from \'@/utils/enum\'\nimport { ${field.enumNameAllUpper}_ENUM } from \'@/enums/${field.enumName}-enum\'\n	</#if>\n</#list>\n\nconst state: IHooksOptions = reactive({\n    dataListUrl: \'/${functionName}/entityPage\',\n    deleteUrl: \'/${functionName}/deleteList\',\n    queryForm: {\n        <#list queryList as field>\n        <#if field.formType == \'date\'>\n        startDate: \'\',\n        endDate: \'\'<#sep>, </#sep>\n        <#elseif field.formType == \'datetime\'>\n        startDateTime: \'\',\n        endDateTime: \'\'<#sep>, </#sep>\n        <#else>\n        ${field.attrName}: \'\'<#sep>, </#sep>\n        </#if>\n        </#list>\n    }\n})\n\nconst queryRef = ref()\nconst addOrUpdateRef = ref()\nconst addOrUpdateHandle = (id: number) => {\n	addOrUpdateRef.value.init(id)\n}\nconst resetQueryRef = () => {\n  queryRef.value.resetFields()\n}\n\nconst { getDataList, selectionChangeHandle, sizeChangeHandle, currentChangeHandle, deleteBatchHandle } = useCrud(state)\n</script>\n', 0, '2025-05-14 17:55:56', '2025-05-21 17:40:17', 0);
